int main(int argc, char const **argv)
{
	ios_base::sync_with_stdio(false);
	if(argc<2)
	{
		cerr << "Usage:\ncr <file name>" << endl;
		return 1;
	}
	string file_name=argv[1];
	// cin >> file_name;
	fstream file(file_name.c_str(), ios::in);
	if(file.good())
	{
		coloring::init();
		// fstream out((file_name+".html").c_str(), ios::out);
		cout << "<table class=\"code_view\">\n<tbody>\n<tr>\n<td>\n<pre>\n1\n";
		string input, tmp;
		getline(file, input);
		unsigned line=1;
		while(file.good())
		{
			cout << ++line << '\n';
			input+='\n';
			getline(file, tmp);
			input+=tmp;
		}
		cout << "</pre>\n</td>\n<td>\n<pre>\n";
		coloring::color_code(input, cout);
		cout << "</pre></td></tr></tbody></table>";
	}
	else cout << "Cannot open file" << endl;
return 0;
}